import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cultureimg2 from "../img/hab-image-2.jpg";
import cultureimg3 from "../img/hab-image-3.jpg";

const ImageCarousel = ({ autoplaySpeeds }) => {
  const [settings, setSettings] = useState({
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    fade: true,
    arrows: false,
    autoplaySpeed: autoplaySpeeds[0] || 5000, // Default speed for the first slide
    speed: 0,
  });

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const handleBeforeChange = (current, next) => {
      setCurrentSlide(next);
    };

    // Update the beforeChange event handler dynamically
    setSettings((prevSettings) => ({
      ...prevSettings,
      beforeChange: handleBeforeChange,
    }));
  }, []);

  const getScale = (index) => {
    // Adjust the scaling factor and duration as needed
    const baseScale = 1;
    const maxScale = 1.2;
    const scaleIncrement = (maxScale - baseScale) / autoplaySpeeds.length;
    const duration = 0.5; // seconds

    const scale = baseScale + scaleIncrement * index;

    return {
      transform: `scale(${scale})`,
      transition: `transform ${duration}s ease`,
    };
  };

  return (
    <Slider {...settings}>
      <div className="col radius-lg" style={getScale(0)}>
        <img src={cultureimg2} alt="Wow look at this" />
      </div>
      <div className="col radius-lg" style={getScale(1)}>
        <img src={cultureimg3} alt="and this" />
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default ImageCarousel;
