import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cultureimg2 from "../img/hab-image-2.jpg";
import cultureimg3 from "../img/hab-image-3.jpg";

const ImageCarousel = ({ autoplaySpeeds }) => {
  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    // Add the slick-animation class to the initially displayed slide
    document
      .querySelector(`.slick-slide[data-index="${currentSlide}"]`)
      .classList.add("slick-animation");
  }, [currentSlide]);

  const [settings, setSettings] = useState({
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    fade: true,
    arrows: false,
    autoplaySpeed: autoplaySpeeds[0] || 5000,
    speed: 0,
    beforeChange: (slick, current, next) => {
      // Add slick-animation class to the displayed slide
      document
        .querySelector(`.slick-slide[data-index="${next}"]`)
        .classList.add("slick-animation");
      // Add stop-animation class to erase slick-animation class from the current slide
      document
        .querySelector(`.slick-slide[data-index="${current}"]`)
        .classList.add("stop-animation");
    },
    afterChange: (slick, current) => {
      // Remove animation classes for slides that are not visible
      document
        .querySelector(`.slick-slide[data-index="${current}"].stop-animation`)
        .classList.remove("stop-animation", "slick-animation");
    },
  });

  useEffect(() => {
    const handleBeforeChange = (slick, current, next) => {
      setSettings((prevSettings) => ({
        ...prevSettings,
        autoplaySpeed: autoplaySpeeds[next] || 5000,
      }));
    };

    // Update the beforeChange event handler dynamically
    setSettings((prevSettings) => ({
      ...prevSettings,
      beforeChange: handleBeforeChange,
    }));
  }, [autoplaySpeeds]);

  return (
    <Slider
      {...settings}
      beforeChange={(slick, current, next) => setCurrentSlide(next)}
    >
      <div className="col radius-lg slide-container">
        <img src={cultureimg2} alt="Wow look at this" />
      </div>
      <div className="col radius-lg slide-container">
        <img src={cultureimg3} alt="and this" />
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default ImageCarousel;
