import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cultureimg2 from "../img/hab-image-2.jpg";
import cultureimg3 from "../img/hab-image-3.jpg";

const ImageCarousel = ({ autoplaySpeeds }) => {
  const [settings, setSettings] = useState({
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    fade: true,
    arrows: false,
    autoplaySpeed: autoplaySpeeds[0] || 5000,
    speed: 0,
    focusOnSelect: false,
    pauseOnHover: false,
  });

  useEffect(() => {
    const handleBeforeChange = (current, next) => {
      // Apply zoom-out animation to the current slide
      const currentSlide = document.querySelector(
        `.slick-slide[data-index="${current}"]`
      );
      if (currentSlide) {
        currentSlide.style.transition = "transform 0.5s";
        currentSlide.style.transform = "scale(1)";
      }

      // Apply zoom-in animation to the next slide
      const nextSlide = document.querySelector(
        `.slick-slide[data-index="${next}"]`
      );
      if (nextSlide) {
        nextSlide.style.transition = "transform 0.5s";
        nextSlide.style.transform = "scale(1.2)";
      }

      // Update the autoplaySpeed dynamically
      setSettings((prevSettings) => ({
        ...prevSettings,
        autoplaySpeed: autoplaySpeeds[next] || 5000,
      }));
    };

    // Update the beforeChange event handler dynamically
    setSettings((prevSettings) => ({
      ...prevSettings,
      beforeChange: handleBeforeChange,
    }));
  }, [autoplaySpeeds]);

  return (
    <Slider {...settings}>
      <div className="col radius-lg slide-container">
        <img src={cultureimg2} alt="Wow look at this" />
      </div>
      <div className="col radius-lg slide-container">
        <img src={cultureimg3} alt="and this" />
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default ImageCarousel;
