import React, { useState, useEffect } from "react";
import { Fade } from "react-reveal";
import GeneratedSignature from "./GeneratedSignature";
import logo from "../img/URGO-logo.jpg";

// Import employee headshots
const employeeHeadshots = {};
const context = require.context(
  "../img/employee_headshots",
  false,
  /\.(jpg|jpeg|png)$/
);
context.keys().forEach((key) => {
  const fileName = key.replace("./", "");
  employeeHeadshots[fileName] = context(key);
});
function EmailSignatureForm() {
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState(true); //Dani change this back to null
  const [copied, setCopied] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    jobTitle: "",
    phoneNumber: "",
    emailAddress: "",
    color: "#D4DFD7",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Check if the input is the phone number field
    if (name === "phoneNumber") {
      // Remove non-numeric characters
      const numericPhoneNumber = value.replace(/\D/g, "");

      // Enforce the character limit of 10
      const truncatedPhoneNumber = numericPhoneNumber.slice(0, 10);

      // Format the truncated phone number as (###) ###-####
      const formattedPhoneNumber = truncatedPhoneNumber.replace(
        /(\d{3})(\d{3})(\d{4})/,
        "($1) $2-$3"
      );

      setFormData({
        ...formData,
        [name]: formattedPhoneNumber,
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    // Construct the filename using the first name and last name
    const fileName = `${formData.firstName
      .toLowerCase()
      .replace(/\s/g, "")}${formData.lastName
      .toLowerCase()
      .replace(/\s/g, "")}H.jpg`;
    // alert(fileName);
    // Check if the image exists in the employee_headshots folder
    if (fileName) {
      import(`../img/employee_headshots/${fileName}`)
        .then((employeeImage) => {
          setTimeout(() => {
            setResults(
              <GeneratedSignature
                fullName={`${formData.firstName} ${formData.lastName}`}
                jobTitle={formData.jobTitle}
                phoneNumber={formData.phoneNumber}
                emailAddress={formData.emailAddress}
                address={formData.address}
                employeeImage={employeeImage.default} // Pass the background image
              />
            );
            setLoading(false);
          }, 1500);
        })
        .catch(() => {
          // Handle the case where the image doesn't exist
          setLoading(false);
          setResults(null);
          alert("Employee headshot not found for this name.");
        });
    }
  };

  const handleGoBack = () => {
    setLoading(true); // Set loading to true

    // Simulate a delay before resetting the form and stopping the loader
    setTimeout(() => {
      setResults(null);
      setCopied(false);
      setFormData({
        firstName: "",
        lastName: "",
        jobTitle: "",
        phoneNumber: "",
        emailAddress: "",
        color: "#D4DFD7",
      });
      setLoading(false); // Set loading back to false after the delay
    }, 1500); // Adjust the timeout duration as needed
  };

  const handleCopyToClipboard = () => {
    setCopied(true);
    const content = document.getElementById("content");
    const range = document.createRange();
    const selection = window.getSelection();

    range.selectNodeContents(content);
    selection.removeAllRanges();
    selection.addRange(range);
  };

  return (
    <div className="container height-100vh">
      {loading ? (
        <section className="height-100vh width-100% position-relative flex justify-center items-center">
          <div className="loader">
            <div className="circle item0"></div>
            <div className="circle item1"></div>
            <div className="circle item2"></div>
          </div>
        </section>
      ) : results ? (
        <>
          <div className="grid gap-lg flex">
            <Fade top cascade>
              <div className="grid gap-md justify-center margin-y-0">
                <div className="col-12 margin-bottom-sm">
                  <div className="flex justify-between items-center padding-y-sm">
                    <img
                      src={logo}
                      alt=""
                      style={{
                        objectFit: "contain",
                        maxWidth: "168px",
                      }}
                    ></img>
                    <h1 className="margin-0 text-lg font-secondary ">
                      Email Signature Generator
                    </h1>
                  </div>
                </div>
              </div>

              <div className="grid gap-md flex margin-top-xl">
                <div className="col ">
                  <Fade top cascade>
                    <div
                      className="tableContainer padding-xl height-100%"
                      style={{
                        background: "#EFF9FB",
                        borderRadius: "30px",
                      }}
                    >
                      <div
                        style={{
                          width: "100%",
                          height: "100%",
                          margin: "0 auto",
                          background: "white",
                          display: "flex",
                          justifyContent: "center",
                          alignItems: "center",
                        }}
                        id="content"
                        className="highlightable"
                      >
                        <table style={{ width: 430, height: 10 }}>
                          <tbody>
                            <tr>
                              <td width={130} rowSpan={10}>
                                <p style={{ textAlign: "center" }}>
                                  <img
                                    width={110}
                                    className="logo"
                                    src="https://staging-agencyhabitat23-staging.kinsta.cloud/wp-content/uploads/2024/01/Abby-Cantwell_Dec2022_Cropped.png"
                                    alt="Logo 1"
                                  />
                                </p>

                                <p>
                                  <img
                                    width={100}
                                    className="logo"
                                    src="https://staging-agencyhabitat23-staging.kinsta.cloud/wp-content/uploads/2024/01/URGO-logo-email_.png"
                                    alt="Logo 2"
                                  />
                                </p>
                              </td>
                              <td
                                width={5}
                                rowSpan={10}
                                style={{ borderRight: "1px solid #003865" }}
                              >
                                &nbsp;
                              </td>
                              <td width={12} rowSpan={10}>
                                &nbsp;
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontWeight: "bold",
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 22,
                                }}
                              >
                                John Appleseed
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 16,
                                  fontWeight: "normal",
                                }}
                              >
                                Chief Executive Officer
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 14,
                                  fontWeight: "normal",
                                }}
                              >
                                Urgo Medical North America
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 8,
                                  fontWeight: "normal",
                                }}
                              >
                                &nbsp;
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 14,
                                  fontWeight: "normal",
                                }}
                              >
                                <a
                                  style={{ color: "#003865" }}
                                  href="tel:0000000000"
                                >
                                  (000) 000-0000
                                </a>
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 14,
                                  fontWeight: "normal",
                                }}
                              >
                                <a
                                  style={{ color: "#003865" }}
                                  href="mailto:john.appleseed@us.urgo.com"
                                >
                                  john.appleseed@us.urgo.com
                                </a>
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 14,
                                  fontWeight: "normal",
                                }}
                              >
                                100 Lexington Street, Suite 400
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 14,
                                  fontWeight: "normal",
                                }}
                              >
                                Fort Worth, TX 76102
                              </td>
                            </tr>
                            <tr>
                              <td
                                style={{
                                  fontFamily: "helvetica, sans-serif",
                                  color: "#003865",
                                  fontSize: 14,
                                  fontWeight: "normal",
                                }}
                              >
                                <a
                                  style={{ color: "#003865" }}
                                  href="https://urgomedical.us"
                                >
                                  urgomedical.us
                                </a>
                              </td>
                            </tr>
                          </tbody>
                        </table>
                        <p>{results}</p>
                      </div>
                    </div>
                  </Fade>
                </div>

                <div className="col">
                  <div
                    className="padding-lg padding-bottom-0 height-100%"
                    style={{
                      borderRadius: "30px",
                      background: "#EFF9FB",
                      color: "#003865",
                    }}
                  >
                    <h2 className="text-md font-secondary margin-bottom-md">
                      Instructions:
                    </h2>
                    <ul className="font-primary instructions">
                      <li className="">Click Copy to Clipboard</li>
                      <li className="">
                        Outlook &gt; Settings &gt; Signatures &gt; Edit
                      </li>
                      <li className="">
                        Delete all the content and paste your email signature
                        &gt; Save
                      </li>
                      <li className="">
                        You can rename the “Standard” email signature to Habitat
                      </li>
                      <li className="">
                        Under “Choose Default Signature” Select your email
                        signature for “New Messages” and “Replies/Forward”
                      </li>
                      <li className="">You're done!</li>
                    </ul>
                  </div>
                  <div className="text-right">
                    <button
                      onClick={handleCopyToClipboard}
                      className="margin-top-md btn btn--primary padding-y-xs margin-right-sm"
                      id="highlightButton"
                    >
                      {copied ? "HTML Copied!" : "Copy to Clipboard"}
                    </button>
                    <Fade top cascade>
                      <button
                        className="margin-top-md btn btn--primary padding-y-xs"
                        onClick={handleGoBack}
                      >
                        Back
                      </button>
                    </Fade>
                  </div>
                </div>
              </div>
            </Fade>
          </div>
        </>
      ) : (
        <>
          <div className="grid gap-lg max-width-xl margin-auto">
            <div className="col margin-top-xl ">
              <Fade left>
                <div className="grid margin-bottom-0 height-100% flex justify-center items-center">
                  <div className="col radius-lg  aspect-ratio-5:4 ">
                    <img
                      src={logo}
                      alt=""
                      style={{
                        objectFit: "contain",
                        maxWidth: "450px",
                      }}
                    ></img>
                  </div>
                </div>
              </Fade>
            </div>

            <div
              className="col margin-top-xl padding-md "
              style={{ backgroundColor: "#EFF9FB", borderRadius: "30px" }}
            >
              <Fade bottom>
                <h1 className="font-secondary text-lg text-center">
                  Email Signature Generator
                </h1>
                <p className="text-center margin-top-xxs">
                  Fill out the form below to create your signature
                </p>
              </Fade>

              <div
                id="emailsigform"
                className="margin-top-md padding-bottom-xs"
              >
                <Fade bottom cascade>
                  <form onSubmit={handleSubmit} className="">
                    <div>
                      <label className="block " htmlFor="first-name">
                        First Name
                      </label>
                      <input
                        type="text"
                        id="first-name"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div>
                      <label className="block" htmlFor="last-name">
                        Last Name
                      </label>
                      <input
                        type="text"
                        id="last-name"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div>
                      <label className="block" htmlFor="job-title">
                        Job Title
                      </label>
                      <input
                        type="text"
                        id="job-title"
                        name="jobTitle"
                        value={formData.jobTitle}
                        onChange={handleChange}
                        required
                      />
                    </div>
                    <div>
                      <label className="block" htmlFor="email">
                        Your Email
                      </label>
                      <input
                        type="text"
                        id="email"
                        name="emailAddress"
                        value={formData.emailAddress}
                        onChange={handleChange}
                      />
                    </div>
                    <div>
                      <label className="block" htmlFor="phone-number">
                        Your Phone Number
                      </label>
                      <input
                        type="tel"
                        id="phone-number"
                        name="phoneNumber"
                        value={formData.phoneNumber}
                        onChange={handleChange}
                      />
                    </div>

                    <div>
                      <button
                        className="btn btn--primary padding-y-xs margin-top-sm"
                        type="submit"
                      >
                        Generate Signature
                      </button>
                    </div>
                  </form>
                </Fade>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}

export default EmailSignatureForm;
