import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cultureimg2 from "../img/hab-image-2.jpg";
import cultureimg3 from "../img/hab-image-3.jpg";
import "./ImageCarousel.css"; // Adjust the path based on your project structure

const ImageCarousel = ({ autoplaySpeeds }) => {
  const [settings, setSettings] = useState({
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    fade: true,
    arrows: false,
    autoplaySpeed: autoplaySpeeds[0] || 5000, // Default speed for the first slide
    speed: 0,
  });

  const [currentSlide, setCurrentSlide] = useState(0);

  useEffect(() => {
    const handleBeforeChange = (current, next) => {
      setCurrentSlide(next);
    };

    // Update the beforeChange event handler dynamically
    setSettings((prevSettings) => ({
      ...prevSettings,
      beforeChange: handleBeforeChange,
    }));
  }, []);

  return (
    <Slider {...settings}>
      <div
        className={`col radius-lg ${currentSlide === 0 ? "scaling-image" : ""}`}
      >
        <img src={cultureimg2} alt="Wow look at this" />
      </div>
      <div
        className={`col radius-lg ${currentSlide === 1 ? "scaling-image" : ""}`}
      >
        <img src={cultureimg3} alt="and this" />
      </div>

      {/* Add more slides as needed */}
    </Slider>
  );
};

export default ImageCarousel;
