import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cultureimg2 from "../img/hab-image-2.jpg";
import cultureimg3 from "../img/hab-image-3.jpg";

const ImageCarousel = ({ autoplaySpeeds }) => {
  const [settings, setSettings] = useState({
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    fade: true,
    arrows: false,
    autoplaySpeed: autoplaySpeeds[0] || 5000, // Default speed for the first slide
    speed: 0, // Set a non-zero speed for the transition
    beforeChange: (current, next) => {
      document
        .querySelector(`.slick-slide[data-index="${current}"]`)
        .classList.remove("scaling");
    },
    afterChange: (current) => {
      document
        .querySelector(`.slick-slide[data-index="${current}"]`)
        .classList.add("scaling");
    },
  });

  useEffect(() => {
    const handleBeforeChange = (current, next) => {
      setSettings((prevSettings) => ({
        ...prevSettings,
        autoplaySpeed: autoplaySpeeds[next] || 5000, // Use the speed for the next slide
      }));
    };

    // Update the beforeChange event handler dynamically
    setSettings((prevSettings) => ({
      ...prevSettings,
      beforeChange: handleBeforeChange,
    }));
  }, [autoplaySpeeds]);

  return (
    <Slider {...settings}>
      <div className="col radius-lg zoom-in">
        <img src={cultureimg2} alt="Wow look at this" />
      </div>
      <div className="col radius-lg zoom-in">
        <img src={cultureimg3} alt="and this" />
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default ImageCarousel;
