import React, { useEffect, useRef } from "react";

const VideoComponent = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    // Ensure the video element exists before attempting to play
    if (videoRef.current) {
      videoRef.current.play().catch((error) => {
        // Autoplay was prevented, handle the error
        console.error("Autoplay failed:", error);
      });
    }
  }, []); // Empty dependency array ensures the effect runs only once after the initial render

  return (
    <video
      muted
      autoPlay
      loop
      playsInline
      ref={videoRef}
      style={{
        height: "60%",
      }}
    >
      <source
        src="https://download-video.akamaized.net/v2-1/playback/ee9411ec-c2bc-488f-8364-605c95f6bdc5/16fff339-af5c95cd?__token__=st=1700586362~exp=1700600762~acl=%2Fv2-1%2Fplayback%2Fee9411ec-c2bc-488f-8364-605c95f6bdc5%2F16fff339-af5c95cd%2A~hmac=9badbb69a2eea26f62fc0d9499faa4f114578e71f16f390c6ed327bbe59b74c9&r=dXMtZWFzdDE%3D"
        type="video/mp4"
      />
    </video>
  );
};

export default VideoComponent;
