import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import cultureimg2 from "../img/hab-image-2.jpg";
import cultureimg3 from "../img/hab-image-3.jpg";

const ImageCarousel = ({ autoplaySpeeds }) => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [animationClass, setAnimationClass] = useState("slick-animation");

  const handleBeforeChange = (_, current, next) => {
    setCurrentSlide(next);
    setAnimationClass("stop-animation");

    setTimeout(() => {
      setAnimationClass("slick-animation");
    }, 0);
  };

  const handleAfterChange = () => {
    setAnimationClass("");
  };

  const settings = {
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    fade: true,
    arrows: false,
    autoplaySpeed: autoplaySpeeds[currentSlide] || 5000,
    speed: 1000,
    beforeChange: handleBeforeChange,
    afterChange: handleAfterChange,
  };

  useEffect(() => {
    setAnimationClass("slick-animation");
  }, [currentSlide]);

  useEffect(() => {
    setSettings((prevSettings) => ({
      ...prevSettings,
      autoplaySpeed: autoplaySpeeds[currentSlide] || 5000,
    }));
  }, [autoplaySpeeds, currentSlide]);

  return (
    <Slider {...settings} className={animationClass}>
      <div className="col radius-lg slide-container">
        <img src={cultureimg2} alt="Wow look at this" />
      </div>
      <div className="col radius-lg slide-container">
        <img src={cultureimg3} alt="and this" />
      </div>
      {/* Add more slides as needed */}
    </Slider>
  );
};

export default ImageCarousel;
